import styled from "styled-components";

const Top = styled.div`
    height: 78px;
    display: flex;
    justify-content: space-between;
    align-items: center;
`

const Header = styled.h1`
    color: #fff;
    font-family: 'Raleway';
    font-weight: 700;
    font-size: 26px;
    line-height: 31px;
`

const Balance = styled.div`
    width: 800vw;
  height: 100px;
  background-color: #fff;
`

const Button = styled.button`
  cursor: pointer;
`

export 
{ 
  Top,
  Header,
  Balance,
  Button
}
