import { useState, useContext, useEffect } from 'react';
import UserContext from '../../contexts/UserContext';
import api from '../../services/api';

import { Button, Teste, Container, Transaction, Description, Balance, H2, Total, Date, Title, Value } from './style';

export default function Transactions() {

  const { token } = useContext(UserContext);
  const [transactions, setTransactions] = useState([]);


  useEffect(() => getTransactions(), []); // eslint-disable-line react-hooks/exhaustive-deps

  function getTransactions(){

    api.getTransactions(token).then(handleSuccess).catch((error) => console.log(error));
  }

  function handleSuccess(response){
    setTransactions(response.data);

  }

  function ShowCashIns(){
    const ins = transactions.filter(obj => obj.type === "in")
    return (
      <>
        {ins.map((t, i) => {     
          return (
            <Transaction key={i}>
            <Description>
            <Date>{`${t.createdAt.slice(8, 10)}/${t.createdAt.slice(5, 7)}`}</Date>
            <Title>{t.type}</Title>
            </Description>
            <Value color={t.type}>R$ {t.value.toFixed(2)}</Value>
            </Transaction> 

            )
        })}
      </>
    )
  }

  function ShowCashOuts(){
    const outs = transactions.filter(obj => obj.type === "out")
    return (
      <>
        {outs.map((t, i) => {     
          return (
            <Transaction key={i}>
            <Description>
            <Date>{`${t.createdAt.slice(8, 10)}/${t.createdAt.slice(5, 7)}`}</Date>
            <Title>{t.type}</Title>
            </Description>
            <Value color={t.type}>R$ {t.value.toFixed(2)}</Value>
            </Transaction> 

            )
        })}
      </>
    )
  }

  return (
    <Container>
      <Teste>
{/*         <Button onClick={showCashOuts}>Cash-Ins</Button>
        <Button onClick={showCashOuts}>Cash-Outs</Button>     */}   

        <ShowCashIns/>

        <ShowCashOuts/>

      </Teste>
      <Balance>
          <H2>SALDO</H2>
          <Total>balance</Total>
      </Balance>
    </Container>
  );
}
