import styled from "styled-components";

const Container = styled.div`

`

const Button = styled.button`
  width: 100%;
  height: 30px;
  border-radius: 6px;
  color: #000;
  font-size: 16px;
  font-weight: 400;
  cursor: pointer;
`
const Form = styled.form`
  width: 100%;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  gap: 4px;

`

const Input = styled.input`
  width: 100%;
  height: 30px;
  border: none;
  border-radius: 6px;
  padding: 5px;
  background-color: #fff;

  ::placeholder{
    font-size: 14px;
    line-height: 40px;
    color: #000;
  }
`

