import { useState, useContext, useEffect } from 'react';
import UserContext from '../../contexts/UserContext';
import api from '../../services/api';

import { Teste, Container, Transaction, Description, Balance, H2, Total, Date, Title, Value } from './style';

export default function Transactions() {

  const { token } = useContext(UserContext);
  const [transactions, setTransactions] = useState([])

  useEffect(() => getTransactions(), []); // eslint-disable-line react-hooks/exhaustive-deps

  function getTransactions(){

    api.getTransactions(token).then((response) => setTransactions(response.data.debitedTransactions)).catch((error) => console.log(error));
  }

 console.log(transactions)

  return (
    <Container>
      <Teste>

        {transactions.map((t, i) => {

          return (
            <Transaction key={i}>
            <Description>
                <Date>{t.createdAt}</Date>
                <Title>type</Title>
            </Description>
            <Value>value</Value>
          </Transaction> 

          )
        })}



      </Teste>
      <Balance>
          <H2>SALDO</H2>
          <Total>balance</Total>
      </Balance>
    </Container>
  );
}
