import { useState, useContext } from 'react';
import UserContext from '../../contexts/UserContext';
import api from '../../services/api';

import { Button} from "./style";

export default function CashOut({ setCashOutBox }) {

  const { userData } = useContext(UserContext);

  const [cashOutData, setCashOutData] = useState({});


  function sendCashOut(e){
    e.preventDefault();

    const data = {...cashOutData, debitedAccountId: userData.accountId}
console.log(data)
    api.cashOut(data).then(handleSuccess).catch((error) => console.log(error));
  }

  function handleSuccess(){
    setCashOutBox(false);
  }

  return (
    <>
        <input
        type="text" 
        placeholder="receiver username"
        value={cashOutData.username || ''}
        onChange={e => setCashOutData({...cashOutData, username: e.target.value})}
        required
        >
        </input>
        <input
        type="number" 
        placeholder="value"
        value={cashOutData.value || ''}
        onChange={e => setCashOutData({...cashOutData, value: e.target.value})}
        required
        >
        </input>
        <Button onClick={sendCashOut}>Send</Button>
    </>
  );
}
