import { useState, useContext, useEffect } from 'react';
import UserContext from '../../contexts/UserContext';
import api from '../../services/api';

import { Teste, Container, Transaction, Description, Balance, H2, Total, Date, Title, Value } from './style';

export default function Transactions() {

  const { token } = useContext(UserContext);
  const [debitedTransactions, setDebitedTransactions] = useState([]);
  const [creditedTransactions, setCreditedTransactions] = useState([]);
  const [transactions, setTransactions] = useState([]);


  useEffect(() => getTransactions(), []); // eslint-disable-line react-hooks/exhaustive-deps

  function getTransactions(){

    api.getTransactions(token).then(handleSuccess).catch((error) => console.log(error));
  }

 function handleSuccess(response){
  setDebitedTransactions(response.data.debitedTransactions);
  setTransactions(...transactions, debitedTransactions);
  setCreditedTransactions(response.data.creditedTransactions);
  setTransactions(...transactions, creditedTransactions);
 }

 console.log(transactions);

  return (
    <Container>
      <Teste>

        {creditedTransactions.map((t, i) => {

          return (
            <Transaction key={i}>
            <Description>
                <Date></Date>
                <Title>type</Title>
            </Description>
            <Value>R$ {t.value.toFixed(2)}</Value>
          </Transaction> 

          )
        })}



      </Teste>
      <Balance>
          <H2>SALDO</H2>
          <Total>balance</Total>
      </Balance>
    </Container>
  );
}
